    setActiveIndex(index);
  };

  return (
    <div className="categories">
      <ul>
        <li
          className={activeIndex === index ? "active" : ""}
          onClick={() => handleClick(0)}
        >
          all
        </li>
        <li onClick={() => handleClick(1)}>art</li>
        <li onClick={() => handleClick(2)}>biography</li>
        <li onClick={() => handleClick(3)}>computers</li>
        <li onClick={() => handleClick(4)}>history</li>
        <li onClick={() => handleClick(5)}>poetry</li>
        <li onClick={() => handleClick(6)}>medical</li>
      </ul>
    </div>
  );
};

export default Categories;
