import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Sort from "../components/Sort";
import BookCard from "../components/BookCard";
import Categories from "../components/Categories";

const Home = () => {
  const [category, setCategory] = useState("all");
  const [books, setBooks] = useState([]);
  const [sortType, setSortType] = useState("relevance");
  const [searchQuery, setSearchQuery] = useState("react");
  const [totalItems, setTotalItems] = useState(0);

  useEffect(() => {
    const fetchBooks = async () => {
      const url = `https://www.googleapis.com/books/v1/volumes?q=${searchQuery}${
        category !== "all" ? `+subject:${category}` : ""
      }&orderBy=${sortType}&startIndex=${
        books.length
      }&maxResults=30&printType=books&projection=lite`;
      const response = await fetch(url);
      const data = await response.json();
      setBooks([...books, ...(data.items || [])]);
      setTotalItems(data.totalItems);
    };
    fetchBooks();
  }, [category, sortType, searchQuery]);

  return (
    <div className="container">
      <div className="content__top">
        <Categories setCategory={setCategory} setSearchQuery={setSearchQuery} />
        <Sort
          sortType={sortType}
          setSortType={setSortType}
          totalItems={totalItems}
        />
      </div>
      <h2 className="content__title">Список книг</h2>
      <div className="content__items">
        {books.map((book) => (
          <BookCard book={book.volumeInfo} key={book.id} />
        ))}
      </div>
      {books.length < totalItems && (
        <div className="content__bottom">
          <button
            className="button button--outline button--add"
            onClick={() => fetchBooks()}
          >
            Загрузить еще
          </button>
        </div>
      )}
    </div>
  );
};

export default Home;
