import React, { useState, useEffect } from "react";
import axios from "axios";
import "./scss/app.scss";
import Categories from "./components/Categories";
import Header from "./components/Header";
import Sort from "./components/Sort";
import BookCard from "./components/BookCard";

function App() {
  const [books, setBooks] = useState([]);
  const [totalItems, setTotalItems] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [selectedSort, setSelectedSort] = useState("relevance");
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  useEffect(() => {
    const fetchBooks = async () => {
      setLoading(true);
      setError(false);

      const startIndex = (currentPage - 1) * 10;
      const maxResults = 10;

      let url = `https://www.googleapis.com/books/v1/volumes?q=${
        selectedCategory !== "all" ? "subject:" + selectedCategory + "+" : ""
      }&orderBy=${selectedSort}&startIndex=${startIndex}&maxResults=${maxResults}&key=[AIzaSyC9DW5law5NpME08n3hBt_6znevkC7TpGU]`;

      try {
        const response = await axios.get(url);
        setBooks(response.data.items);
        setTotalItems(response.data.totalItems);
        setLoading(false);
      } catch (error) {
        console.log(error);
        setError(true);
        setLoading(false);
      }
    };

    fetchBooks();
  }, [currentPage, selectedCategory, selectedSort]);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    setCurrentPage(1);
  };

  const handleSortChange = (sort) => {
    setSelectedSort(sort);
    setCurrentPage(1);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="wrapper">
      <Header />
      <div className="content">
        <div className="container">
          <div className="content__top">
            <Categories
              selectedCategory={selectedCategory}
              handleCategoryChange={handleCategoryChange}
            />
            <Sort
              selectedSort={selectedSort}
              handleSortChange={handleSortChange}
            />
          </div>
          {loading && <div>Loading...</div>}
          {error && <div>Error!</div>}
          {!loading && !error && (
            <>
              <h2 className="content__title">Search Results</h2>
              <div className="content__items">
                {books.map((book) => (
                  <BookCard book={book.volumeInfo} key={book.id} />
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
