import React, { useState, useEffect } from "react";
import "./scss/app.scss";
import Categories from "./components/Categories";
import Header from "./components/Header";
import Sort from "./components/Sort";
import BookCard from "./components/BookCard";

function App() {
  const [category, setCategory] = useState("all"); // храним выбранную категорию
  const [books, setBooks] = useState([]); // храним список книг

  useEffect(() => {
    // делаем запрос к Google Books API и обновляем список книг
    const fetchBooks = async () => {
      const url =
        category === "all"
          ? "https://www.googleapis.com/books/v1/volumes?q=react&key=YOUR_API_KEY"
          : `https://www.googleapis.com/books/v1/volumes?q=react+subject:${category}&key=YOUR_API_KEY`;
      const response = await fetch(url);
      const data = await response.json();
      setBooks(data.items);
    };
    fetchBooks();
  }, [category]);

  return (
    <div className="wrapper">
      <Header />
      <div className="content">
        <div className="container">
          <div className="content__top">
            <Categories setCategory={setCategory} />
            <Sort />
          </div>
          <h2 className="content__title">Список книг</h2>
          <div className="content__items">
            {books &&
              books.map((book) => (
                <BookCard book={book.volumeInfo} key={book.id} />
              ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
