import React from "react";

const BookCard = ({ book }) => {
  const { imageLinks, title, authors, categories, language, id } = book;
  return (
    <div className="book-block">
      <img
        className="book-block__image"
        src={imageLinks?.smallThumbnail || ""}
        alt={title}
      />
      <h4 className="book-block__title">{title}</h4>
      <p className="book-block__authors">{authors?.join(", ")}</p>
      <p className="book-block__categories">{categories?.join(", ")}</p>
      <div className="book-block__bottom">
        <div className="book-block__price">{language}</div>
        <div
          className="button button--outline button--add"
          onClick={() => (window.location.href = `/books/${id}`)}
        >
          <span>Добавить</span>
        </div>
      </div>
    </div>
  );
};

export default BookCard;
