import React from "react";

const Categories = ({ setCategory }) => {
  const [activeIndex, setActiveIndex] = React.useState(0);
  const onClickCategory = (index) => {
    setActiveIndex(index);
  };
  const handleClick = (category) => {
    setCategory(category);
  };

  return (
    <div className="categories">
      <ul>
        <li
          className={activeIndex === index ? "active" : ""}
          onClick={() => handleClick("all")}
          onChange={() => onClickCategory(0)}
        >
          all
        </li>
        <li
          onChange={() => onClickCategory(1)}
          onClick={() => handleClick("art")}
        >
          art
        </li>
        <li
          onChange={() => onClickCategory(2)}
          onClick={() => handleClick("biography")}
        >
          biography
        </li>
        <li onClick={() => handleClick("computers")}>computers</li>
        <li onClick={() => handleClick("history")}>history</li>
        <li onClick={() => handleClick("poetry")}>poetry</li>
        <li onClick={() => handleClick("medical")}>medical</li>
      </ul>
    </div>
  );
};

export default Categories;
