      const data = await response.json();
      setBooks(data.items);
    } catch (error) {
      console.error(error);
    }
    setLoading(false);
  };

  return (
    <div className="wrapper">
      <Header onSearch={handleSearch} />
      <div className="content">
        {loading ? (
          <p>Loading...</p>
        ) : (
          <Routes>
            <Route path="/" element={<Home books={books} />} />
            <Route path="/about/:id" element={<BookDetails />} />
          </Routes>
        )}
      </div>
    </div>
  );
}

export default App;
